## ViewModel 

`ViewModel` 



## Demo

```dart
import 'package:bili_livedata/bili_livedata.dart';
import 'package:flutter/material.dart';

void main() => runApp(MyApp());

class MyApp extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Flutter Demo',
      theme: ThemeData(
        primarySwatch: Colors.blue,
      ),
      home: MyHomePage(title: 'Flutter Demo Home Page'),
    );
  }
}

/// mixin ViewModel
mixin HomePageViewModel {
  ///  LiveData
  final LiveData<int> counterLiveData = LiveData();

  ///  [counterLiveData]  [add] 
  void counterAdd(int add) {
    if (counterLiveData.value == null) {
      counterLiveData.value = 0;
    } else {
      counterLiveData.value = counterLiveData.value + add;
    }
  }
}

//  with  HomePageViewModel
class MyHomePage extends StatelessWidget with HomePageViewModel {
  MyHomePage({Key key, this.title}) : super(key: key);

  final String title;

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text(title),
      ),
      body: Center(
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          children: <Widget>[
            Text(
              'You have pushed the button this many times:',
            ),
            /// LiveDataWidget 
            LiveDataWidget<LiveData<int>>(
                data: counterLiveData, // HomePageViewModel  counterLiveData
                builder: (context, value, _) {
                  return Text(value.value.toString()); //  Text  counterLiveData 
                }),
          ],
        ),
      ),
      floatingActionButton: FloatingActionButton(
        onPressed: () {
          counterAdd(2); //  HomePageViewModel  counterAdd 
        },
        tooltip: 'Increment',
        child: Icon(Icons.add),
      ),
    );
  }
}

